// server.js
const express = require("express");
const multer = require("multer");
const XLSX = require("xlsx");
const csv = require("csv-parser");
const fs = require("fs");
const path = require("path");
const cors = require("cors");
const { MongoClient } = require("mongodb");
const PDFDocument = require("pdfkit");

const app = express();
const PORT = 5000;

// ====== MongoDB ======
const MONGO_URI = "mongodb://127.0.0.1:27017";
const DB_NAME = "dashboard_db";
let db;
MongoClient.connect(MONGO_URI, { useUnifiedTopology: true })
  .then((client) => {
    db = client.db(DB_NAME);
    console.log("âœ… Connected to MongoDB");
  })
  .catch((err) => console.error("âŒ MongoDB connection failed:", err));

// ====== Middleware ======
app.use(cors());
app.use(express.json());
const upload = multer({ dest: "uploads/" });

// ====== Helpers ======
const statusList = [
  "all",
  "rto",
  "door_step_exchanged",
  "delivered",
  "cancelled",
  "ready_to_ship",
  "shipped",
  "supplier_listed_price",
  "supplier_discounted_price",
];

// Clean any currency/number field
function parsePrice(value) {
  if (!value) return 0;
  const clean = value.toString().trim().replace(/[^0-9.\-]/g, "");
  return parseFloat(clean) || 0;
}

// Get a value using multiple possible column names (case/space tolerant)
function getColumnValue(row, possibleNames) {
  const keys = Object.keys(row);
  const lower = keys.map((k) => k.toLowerCase().trim());
  for (let name of possibleNames) {
    const idx = lower.indexOf(name.toLowerCase().trim());
    if (idx !== -1) return row[keys[idx]];
  }
  return 0;
}

// Try to parse date from multiple common columns & formats
function getOrderDate(row) {
  const possibleDateCols = [
    "Order Date",
    "Order date",
    "Date",
    "Created At",
    "Created at",
    "Delivered Date",
    "Order Placed Time",
    "Order Created Date",
  ];
  for (let col of possibleDateCols) {
    if (row[col]) {
      const d = normalizeToDate(row[col]);
      if (d) return d;
    }
  }
  return null;
}

// Accepts values like "2025-07-03", "03/07/2025", etc.
function normalizeToDate(val) {
  if (val == null || val === "") return null;

  // Excel serial date handling
  if (typeof val === "number") {
    const excelEpoch = new Date(Date.UTC(1899, 11, 30));
    const ms = val * 24 * 60 * 60 * 1000;
    return new Date(excelEpoch.getTime() + ms);
  }

  let s = String(val).trim();

  const parsed = Date.parse(s);
  if (!Number.isNaN(parsed)) return new Date(parsed);

  const m = s.match(/^(\d{1,2})[\/-](\d{1,2})[\/-](\d{2,4})$/);
  if (m) {
    const dd = parseInt(m[1], 10);
    const mm = parseInt(m[2], 10) - 1;
    let yyyy = parseInt(m[3], 10);
    if (yyyy < 100) yyyy += 2000;
    const d = new Date(yyyy, mm, dd);
    if (!isNaN(d.getTime())) return d;
  }

  return null;
}

function toDateKey(d) {
  const yr = d.getFullYear();
  const mo = String(d.getMonth() + 1).padStart(2, "0");
  const da = String(d.getDate()).padStart(2, "0");
  return `${yr}-${mo}-${da}`;
}

// ====== Core Categorization (with Daily Profit) ======
function categorizeRows(rows) {
  const categories = {};
  statusList.forEach((status) => (categories[status] = []));
  categories.other = [];

  let totalSupplierListedPrice = 0;
  let totalSupplierDiscountedPrice = 0;
  let sellInMonthProducts = 0;
  let deliveredSupplierDiscountedPriceTotal = 0;
  let totalDoorStepExchanger = 0;

  const dailyProfits = {};
  const sellingPrice = 500; // fixed selling price per product

  rows.forEach((row) => {
    const status = (row["Reason for Credit Entry"] || "").toLowerCase().trim();
    categories["all"].push(row);

    const listedPrice = parsePrice(
      getColumnValue(row, [
        "Supplier Listed Price (Incl. GST + Commission)",
        "Supplier Listed Price",
        "Listed Price",
      ])
    );

    const discountedPrice = parsePrice(
      getColumnValue(row, [
        "Supplier Discounted Price (Incl GST and Commission)",
        "Supplier Discounted Price (Incl GST and Commision)",
        "Supplier Discounted Price",
        "Discounted Price",
      ])
    );

    totalSupplierListedPrice += listedPrice;
    totalSupplierDiscountedPrice += discountedPrice;

    const orderDate = getOrderDate(row);
    const dateKey = orderDate ? toDateKey(orderDate) : "unknown";

    if (status.includes("delivered")) {
      sellInMonthProducts += 1;
      deliveredSupplierDiscountedPriceTotal += discountedPrice;

      // âœ… Profit always positive: selling price - discounted price
      const profit = sellingPrice - discountedPrice;
      if (!dailyProfits[dateKey]) dailyProfits[dateKey] = 0;
      dailyProfits[dateKey] += profit;
    }

    if (status.includes("door_step_exchanged")) {
      totalDoorStepExchanger += 80;
    }

    let matched = false;
    if (
      status.includes("rto_complete") ||
      status.includes("rto_locked") ||
      status.includes("rto_initiated")
    ) {
      categories["rto"].push(row);
      matched = true;
    } else {
      statusList.forEach((s) => {
        if (s !== "all" && s !== "rto" && status.includes(s)) {
          categories[s].push(row);
          matched = true;
        }
      });
    }

    if (!matched) categories.other.push(row);
  });

  // âœ… Correct total profit: sum of per-order profits
  const totalProfit =
    sellInMonthProducts * sellingPrice - deliveredSupplierDiscountedPriceTotal;

  const profitPercent =
    sellInMonthProducts !== 0
      ? (totalProfit / (sellInMonthProducts * sellingPrice)) * 100
      : 0;

  categories.totals = {
    totalSupplierListedPrice,
    totalSupplierDiscountedPrice,
    sellInMonthProducts,
    deliveredSupplierDiscountedPriceTotal,
    totalDoorStepExchanger,
    totalProfit,
    profitPercent: profitPercent.toFixed(2),
    dailyProfits,
  };

  return categories;
}

// ====== Upload ======
app.post("/upload", upload.single("file"), async (req, res) => {
  const file = req.file;
  if (!file) return res.status(400).json({ error: "No file uploaded" });

  const ext = path.extname(file.originalname).toLowerCase();
  let rows = [];

  try {
    if (ext === ".csv") {
      fs.createReadStream(file.path)
        .pipe(csv())
        .on("data", (data) => rows.push(data))
        .on("end", async () => {
          fs.unlinkSync(file.path);
          await saveToDB(rows, res);
        })
        .on("error", async (err) => {
          try {
            fs.unlinkSync(file.path);
          } catch {}
          console.error("âŒ CSV read error:", err);
          return res.status(500).json({ error: "Failed to read CSV" });
        });
    } else if (ext === ".xlsx" || ext === ".xls") {
      const workbook = XLSX.readFile(file.path);
      const sheetName = workbook.SheetNames[0];
      rows = XLSX.utils.sheet_to_json(workbook.Sheets[sheetName], { raw: true });
      fs.unlinkSync(file.path);
      await saveToDB(rows, res);
    } else {
      fs.unlinkSync(file.path);
      return res.status(400).json({ error: "Unsupported file format" });
    }
  } catch (error) {
    try {
      fs.unlinkSync(file.path);
    } catch {}
    console.error("âŒ Error processing file:", error);
    return res.status(500).json({ error: "Failed to process file" });
  }
});

async function saveToDB(rows, res) {
  if (!db) return res.status(500).json({ message: "MongoDB not connected yet" });
  if (!rows || !rows.length)
    return res.status(400).json({ message: "No data to save" });

  const categorized = categorizeRows(rows);

  try {
    await db.collection("dashboard_data").insertOne({
      submittedAt: new Date(),
      data: rows,
      totals: categorized.totals,
      categories: categorized,
    });
    console.log("âœ… Uploaded data inserted into MongoDB");
    return res.json(categorized);
  } catch (error) {
    console.error("âŒ Error saving uploaded data to MongoDB:", error);
    return res.status(500).json({ message: "Failed to save data to MongoDB" });
  }
}

// ====== Profit Graph API ======
app.get("/profit-graph", async (req, res) => {
  try {
    const result = await db
      .collection("dashboard_data")
      .find()
      .sort({ submittedAt: -1 })
      .limit(1)
      .toArray();

    if (!result.length) return res.status(404).json({ error: "No data found" });

    const dailyProfits = result[0].totals?.dailyProfits || {};
    let data = Object.entries(dailyProfits)
      .filter(([date]) => date !== "unknown")
      .map(([date, profit]) => ({ date, profit: Number(profit || 0) }))
      .sort((a, b) => new Date(a.date) - new Date(b.date));

    const { from, to } = req.query;
    if (from || to) {
      data = data.filter((item) => {
        const d = new Date(item.date);
        const okFrom = from ? d >= new Date(from) : true;
        const okTo = to ? d <= new Date(to) : true;
        return okFrom && okTo;
      });
    }

    res.json(data);
  } catch (err) {
    console.error("âŒ Profit graph API error:", err);
    res.status(500).json({ error: "Failed to fetch profit graph data" });
  }
});

// ====== Start ======
app.listen(PORT, () =>
  console.log(`ðŸš€ Server running on http://localhost:${PORT}`)
);
