import React, { useState } from "react";
import axios from "axios";
import "./Excel.css";

function App() {
  const [file, setFile] = useState(null);
  const [subOrderNo, setSubOrderNo] = useState("");
  const [filterResult, setFilterResult] = useState(null);

  const [data, setData] = useState({
    all: 0,
    rto: 0,
    door_step_exchanged: 0,
    delivered: 0,
    cancelled: 0,
    ready_to_ship: 0,
    shipped: 0,
    other: 0,
    totalSupplierListedPrice: 0,
    totalSupplierDiscountedPrice: 0,
    sellInMonth: 0,
    totalProfit: 0,
    deliveredSupplierDiscountedPriceTotal: 0,
    totalDoorStepExchanger: 0,
  });

  const [profitPercent, setProfitPercent] = useState(0);
  const [dragActive, setDragActive] = useState(false);
  const [showFilteredView, setShowFilteredView] = useState(false);

  // âœ… File selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    if (
      selectedFile &&
      (selectedFile.name.endsWith(".csv") ||
        selectedFile.name.endsWith(".xlsx") ||
        selectedFile.name.endsWith(".xls"))
    ) {
      setFile(selectedFile);
    } else {
      alert("Please upload a valid CSV or Excel file");
    }
  };

  // âœ… Drag & Drop
  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    const droppedFile = e.dataTransfer.files[0];
    if (
      droppedFile &&
      (droppedFile.name.endsWith(".csv") ||
        droppedFile.name.endsWith(".xlsx") ||
        droppedFile.name.endsWith(".xls"))
    ) {
      setFile(droppedFile);
    } else {
      alert("Only .csv or .xlsx files are supported");
    }
  };
  const handleDragOver = (e) => { e.preventDefault(); setDragActive(true); };
  const handleDragLeave = () => setDragActive(false);

  // âœ… Filter SubOrderNo
  const handleFilter = async () => {
    if (!subOrderNo) {
      alert("Please enter a Sub Order No.");
      return;
    }
    try {
      const res = await axios.get(`http://localhost:5000/filter/${subOrderNo}`);
      setFilterResult(res.data);

      // Profit % (fixed selling price â‚¹500)
      const sellingPrice = 500;
      const profit = sellingPrice - res.data.discountedPrice;
      const calcProfitPercent = (profit / sellingPrice) * 100;

      setProfitPercent(calcProfitPercent.toFixed(2));
      setShowFilteredView(true);
    } catch (err) {
      console.error("Filter failed", err);
      alert("No matching sub order found");
    }
  };

  // âœ… Upload file â†’ backend
  const handleSubmitAll = async () => {
    if (!file) {
      alert("Please select a file first");
      return;
    }
    try {
      const formData = new FormData();
      formData.append("file", file);

      const uploadRes = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const result = uploadRes.data;

      const updatedData = {
        all: result.all?.length || 0,
        rto: result.rto?.length || 0,
        door_step_exchanged: result.door_step_exchanged?.length || 0,
        delivered: result.delivered?.length || 0,
        cancelled: result.cancelled?.length || 0,
        ready_to_ship: result.ready_to_ship?.length || 0,
        shipped: result.shipped?.length || 0,
        other: result.other?.length || 0,
        totalSupplierListedPrice: result.totals?.totalSupplierListedPrice || 0,
        totalSupplierDiscountedPrice: result.totals?.totalSupplierDiscountedPrice || 0,
        sellInMonth: result.totals?.sellInMonthProducts || 0,
        totalProfit: result.totals?.totalProfit || 0,
        deliveredSupplierDiscountedPriceTotal:
          result.totals?.deliveredSupplierDiscountedPriceTotal || 0,
        totalDoorStepExchanger: result.totals?.totalDoorStepExchanger || 0,
      };
      setData(updatedData);

      // âœ… Correct Profit % calc
      const sellingPrice = 500;
      const totalRevenue = updatedData.sellInMonth * sellingPrice;
      const calcProfitPercent =
        totalRevenue > 0 ? (updatedData.totalProfit / totalRevenue) * 100 : 0;

      setProfitPercent(calcProfitPercent.toFixed(2));
      alert("File processed and data saved to MongoDB!");
    } catch (err) {
      console.error("Submit all failed", err);
      alert("Failed to process & store data");
    }
  };

  // âœ… Download PDF
  const handleDownloadPDF = async () => {
    try {
      const response = await axios.get("http://localhost:5000/download-pdf", {
        responseType: "blob",
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "report.pdf");
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("Error downloading PDF", err);
      alert("Failed to download PDF");
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-logo">Meesho</div>
        <div className="navbar-search">
          <input
            type="search"
            placeholder="Enter Sub Order No"
            value={subOrderNo}
            onChange={(e) => setSubOrderNo(e.target.value)}
          />
          <button className="filter-btn" onClick={handleFilter}>Filter</button>
          {showFilteredView && (
            <button className="back-btn" onClick={() => setShowFilteredView(false)}>Back</button>
          )}
        </div>
      </nav>

      <h1 className="heading">ðŸ“Š Product Status Dashboard</h1>

      {/* Dashboard */}
      {!showFilteredView ? (
        <div className="status-boxes">
          <div className="box all">All<br /><span>{data.all}</span></div>
          <div className="box rto">RTO<br /><span>{data.rto}</span></div>
          <div className="box door_step_exchanged">
            Door Step Exchanged<br /><span>{data.door_step_exchanged}</span><br/>
            <small>â‚¹{data.totalDoorStepExchanger.toLocaleString()}</small>
          </div>
          <div className="box delivered">
            Delivered<br /><span>{data.delivered}</span><br/>
            <small>â‚¹{data.deliveredSupplierDiscountedPriceTotal.toLocaleString()}</small>
          </div>
          <div className="box cancelled">Cancelled<br /><span>{data.cancelled}</span></div>
          <div className="box ready_to_ship">Pending<br /><span>{data.ready_to_ship}</span></div>
          <div className="box shipped">Shipped<br /><span>{data.shipped}</span></div>
          <div className="box other">Other<br /><span>{data.other}</span></div>
          <div className="box other">Supplier Listed Price<br /><span>â‚¹{data.totalSupplierListedPrice.toLocaleString()}</span></div>
          <div className="box other">Supplier Discounted Price<br /><span>â‚¹{data.totalSupplierDiscountedPrice.toLocaleString()}</span></div>
          <div className="box other">Total Profit<br /><span>â‚¹{data.totalProfit.toLocaleString()}</span></div>
          <div className="box other">Profit %<br /><span>{profitPercent}%</span></div>
        </div>
      ) : (
        filterResult && (
          <div className="status-boxes">
            <div className="box other">Supplier Listed Price<br /><span>â‚¹{filterResult.listedPrice.toLocaleString()}</span></div>
            <div className="box other">Supplier Discounted Price<br /><span>â‚¹{filterResult.discountedPrice.toLocaleString()}</span></div>
            <div className="box other">Profit (per product)<br /><span>â‚¹{(500 - filterResult.discountedPrice).toLocaleString()}</span></div>
            <div className="box other">Profit %<br /><span>{profitPercent}%</span></div>
          </div>
        )
      )}

      {/* File Upload */}
      <div
        className={`upload-section ${dragActive ? "drag-active" : ""}`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      >
        <p>Drag & drop your CSV/Excel file here</p>
        <input type="file" accept=".csv,.xlsx,.xls" onChange={handleFileChange} />
        {file && <p className="filename">ðŸ“‚ {file.name}</p>}
      </div>

      {/* Actions */}
      <div className="actions">
        <button onClick={handleSubmitAll} className="btn success">Submit All</button>
        <button onClick={handleDownloadPDF} className="btn primary">ðŸ“¥ Download PDF</button>
      </div>
    </div>
  );
}

export default App;
